INSERT INTO `oa_supplier` (`id`, `name`, `country`, `prov`, `city`, `kind`, `contact`, `tel`, `desc`, `input_uid`, `input_uname`, `input_time`, `audit_status`, `status`, `memo`) VALUES
(2, '北京饭店', '中国', '北京', '北京', 1, '张志华', '13800138000', '<p>北京饭店位于北京市中心，毗邻昔日皇宫紫禁城，漫步五分钟即可抵达天安门、人民大会堂、国家大剧院及其它历史文化景点，与繁华的王府井商业街仅咫尺之遥。</p>\r\n\r\n<p>位于北京市中心长安街的北京饭店是座历史悠久的大型豪华饭店，紧邻市中心王府井商业街。饭店交融了东西方文化，曾接待过多个国家和地区的首脑。饭店房间高大宽敞，豪华典雅。饭店拥有各类中西餐厅及风味独特的谭家菜。饭店周边完善的地面及地铁交通设施为宾客的商务出行节省了宝贵的时间，同时现代化的酒店会议设施及服务可为宾客商务会谈和宴请提供更加广泛的空间。北京饭店是2008北京奥运大家庭总部饭店，连续多年荣获了由美国优质服务科学学会颁发的&ldquo;五星钻石奖&rdquo;。</p>\r\n\r\n<p>饭店餐厅为客人提供风味独特的谭家菜、四川菜、淮扬菜、粤菜、西餐和日餐、功能完备的宴会厅、多功能厅、会议室、商务中心以及健身、游泳、水疗、室内外网球场、保龄球馆、台球室、壁球和棋牌室。此外，饭店还提供送餐、洗衣、美容美发、外币兑换、邮局、机票代理、互联网、贵宾车队及管家服务等，完善的设施把饭店、生活、时尚三者紧密结合在了一起。</p>\r\n\r\n<p>北京饭店还连续多年荣获了由美国优质服务科学学会颁发的&ldquo;五星钻石奖&rdquo;，此奖项是酒店业国际最高荣誉。</p>\r\n\r\n<p>坐落于雄伟壮丽的天安门东侧、毗邻市中心商业区，步行五分种即可达皇城紫禁城。从1900年创办至今，它那舒适豪华的居室，独具特色的佳肴美馔，热情周到的服务，无数名人的下榻，时刻吸引着海内外人士的目光。</p>\r\n', 1, '开发测试', 1436667282, 0, 0, 0),
(3, '神州租车', '中国', '北京', '北京', 3, '张启', '18611000302', '<p>神州租车 (CAR Inc.)成立于2007年9月，总部位于中国北京。作为中国汽车租赁行业的领跑者，神州租车积极借鉴国际成熟市场成功的汽车租赁模式，并结合中国客户的消费习惯，为广大消费者提供短租、长租及融资租赁等专业化的汽车租赁服务，以及GPS 导航、道路救援等完善的配套服务。</p>\r\n\r\n<p>神州租车(CAR Inc.，简称CAR)作为中国汽车租赁行业的领跑者，神州租车积极借鉴国际成熟市场成功的汽车租赁模式，并结合中国客户的消费习惯，为广大消费者提供专业化的汽车租赁服务，以及完善的各项增值服务。</p>\r\n\r\n<p>神州租车坚持以客户为本的专业态度，颠覆繁琐的传统租车模式，为客户提供了快速便捷的全新租车服务体验。公司遍布在中国大陆各主要城市及旅游地区的服务网络，以及，专业化的24小时的取还车服务和客户服务，不仅可以随时随地满足客户的租车服务需求，更可为客户的安全行车保驾护航。</p>\r\n', 1, '开发测试', 1437926278, 1, 0, 0),
